mapper.select:
SELECT * FROM BOOK_EBOOK_DATABIND
WHERE ${searchType} LIKE '%'||#{keyword}||'%'
AND CATEGORY_CODE=#{categoryCode}
ORDER BY 


		PUBDATE ${pubdateOrder},
		SALES_VOLUME ${salesOrder},
		PRICE ${priceOrder}


		SALES_VOLUME ${salesOrder},
		PUBDATE ${pubdateOrder},
		PRICE ${priceOrder}


		SALES_VOLUME ${salesOrder},
		PRICE ${priceOrder},
		PUBDATE ${pubdateOrder}


		PRICE ${priceOrder},
		SALES_VOLUME ${salesOrder},
		PUBDATE ${pubdateOrder}


		PUBDATE ${pubdateOrder},
		PRICE ${priceOrder},
		SALES_VOLUME ${salesOrder}


		PRICE ${priceOrder},
		PUBDATE ${pubdateOrder},
		SALES_VOLUME ${salesOrder}
